import React, { useState } from "react";
import { ReactComponent as Logo } from '../../images/logo.svg';
import Menu from '../menu/menu';
import styles from './header.module.css'
import { HamburgerSpin } from 'react-animated-burgers'

export default function Header(){
    const [menuActive, setMenuActive] = useState(false);
    const [isActive, setIsActive] = useState(false);

   const toggleButton = () => setIsActive(!isActive);

    return(
        <header className={styles.container}>
        <div >
            <Logo className={styles.logo}/>
        </div>
        <div id="header" className={styles.burgerMenuContainer}>
        {/* <HamburgerSpin className={styles.burgerBtn} isActive={isActive} buttonStyle={{backgroundColor:"#FFFFFF"}}  onClick={() => (setMenuActive(!menuActive), setIsActive(!isActive))}/> */}
        <button class="menu" onclick="this.classList.toggle('opened');this.setAttribute('aria-expanded', this.classList.contains('opened'))" aria-label="Main Menu">
      <svg width="100" height="100" viewBox="0 0 100 100">
        <path class="line line1" d="M 20,29.000046 H 80.000231 C 80.000231,29.000046 94.498839,28.817352 94.532987,66.711331 94.543142,77.980673 90.966081,81.670246 85.259173,81.668997 79.552261,81.667751 75.000211,74.999942 75.000211,74.999942 L 25.000021,25.000058" />
        <path class="line line2" d="M 20,50 H 80" />
        <path class="line line3" d="M 20,70.999954 H 80.000231 C 80.000231,70.999954 94.498839,71.182648 94.532987,33.288669 94.543142,22.019327 90.966081,18.329754 85.259173,18.331003 79.552261,18.332249 75.000211,25.000058 75.000211,25.000058 L 25.000021,74.999942" />
      </svg>
    </button>
        {/* <nav>
            
          <div
            className={styles.burgerBtn}
            onClick={() => setMenuActive(!menuActive)}
          >
            <span />
          </div>
        </nav> */}
        <Menu active={menuActive} setActive={setMenuActive} isActive={isActive} setIsActive={setIsActive} />
      </div>
        </header>
    )
}
