import React, { useState } from "react";
import { ReactComponent as Logo } from '../../images/logo.svg';
import Menu from '../menu/menu';
import styles from './header.module.css'
import { HamburgerSpin } from 'react-animated-burgers'
import { ReactComponent as Arrow } from '../../images/arrow.svg';


export default function Header(){
    const [menuActive, setMenuActive] = useState(false);
    const [isActive, setIsActive] = useState(false);

   const toggleButton = () => setIsActive(!isActive);

    return(
        <header className={styles.container}>
        <div >
            <Logo className={styles.logo}/>
        </div>
        <div> 
            <Arrow className={styles.arrow}/>
        </div>
        <div id="header" className={styles.burgerMenuContainer}>
        <HamburgerSpin className={styles.burgerBtn} isActive={isActive} buttonStyle={{backgroundColor:"#FFFFFF", padding:0, width:'32px'}} buttonWidth={20}  onClick={() => (setMenuActive(!menuActive), setIsActive(!isActive))}/>
        <Menu active={menuActive} setActive={setMenuActive} isActive={isActive} setIsActive={setIsActive} />
      </div>
        </header>
    )
}
